// src/components/PaymentForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const PaymentForm = () => {
  const [bills, setBills] = useState([]);  // List of bills
  const [selectedBill, setSelectedBill] = useState('');  // Selected bill ID
  const [paymentAmount, setPaymentAmount] = useState('');  // Payment amount
  const [paymentMethod, setPaymentMethod] = useState('credit_card');  // Payment method
  const navigate = useNavigate();

  // Fetch bills from the backend
  useEffect(() => {
    const fetchBills = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/bills/', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('access_token')}`,
          },
        });
        setBills(response.data);
      } catch (error) {
        console.error('Failed to fetch bills:', error);
      }
    };
    fetchBills();
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://127.0.0.1:8000/api/payments/',
        {
          bill: selectedBill,
          payment_amount: paymentAmount,
          payment_method: paymentMethod,
          payment_date: new Date().toISOString().split('T')[0],  // Current date in YYYY-MM-DD format
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('access_token')}`,
          },
        }
      );
      alert('Payment successful!');
      navigate('/dashboard');  // Redirect to the dashboard
    } catch (error) {
      console.error('Payment failed:', error);
      alert('Payment failed. Please try again.');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Make a Payment</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="bill" className="form-label">Select Bill</label>
          <select
            className="form-control"
            id="bill"
            value={selectedBill}
            onChange={(e) => setSelectedBill(e.target.value)}
            required
          >
            <option value="">Select a bill</option>
            {bills.map((bill) => (
              <option key={bill.id} value={bill.id}>
                Bill #{bill.id} - Amount: ${bill.total_amount} (Due: {bill.due_date})
              </option>
            ))}
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="paymentAmount" className="form-label">Payment Amount</label>
          <input
            type="number"
            className="form-control"
            id="paymentAmount"
            value={paymentAmount}
            onChange={(e) => setPaymentAmount(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="paymentMethod" className="form-label">Payment Method</label>
          <select
            className="form-control"
            id="paymentMethod"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
            required
          >
            <option value="credit_card">Credit Card</option>
            <option value="mobile_money">Mobile Money</option>
            <option value="bank_transfer">Bank Transfer</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary">Submit Payment</button>
      </form>
    </div>
  );
};

export default PaymentForm;
