import React from 'react';
import axios from 'axios';

const AdminExportBills = () => {
  const handleExport = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await axios.get('http://127.0.0.1:8000/api/admin/export-bills/', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob', // Important for binary data
      });

      // Create a URL for the blob and trigger the download
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'bills_report.xlsx');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error('Failed to export bills:', error);
      alert('Failed to export bills report.');
    }
  };

  return (
    <div className='allocation-container'>
      <h2>Allocate Bill</h2>
      <form onSubmit={handleSubmit}>
        <div className="select-user">
          <label htmlFor="userSelect" className="form-label">Select User</label>
          <select
            id="userSelect"
            className="form-control"
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.target.value)}
            required
          >
            <option value="">Select a user</option>
            {users.map((user) => (
              // Ensure each option has a unique key
              <option key={user.id} value={user.id}>
                {user.username} ({user.email})
              </option>
            ))}
          </select>
        </div>
        <div className="total-amount">
          <label htmlFor="totalAmount" className="form-label">Total Amount</label>
          <input
            type="number"
            step="0.01"
            id="totalAmount"
            className="form-control"
            value={totalAmount}
            onChange={(e) => setTotalAmount(e.target.value)}
            required
          />
        </div>
        <div className="due-date">
          <label htmlFor="dueDate" className="form-label">Due Date</label>
          <input
            type="date"
            id="dueDate"
            className="form-control"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn-allocate-bill">Allocate Bill</button>
      </form>
    </div>
  );
};

export default AdminExportBills;
