// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [bills, setBills] = useState([]);

  useEffect(() => {
    const fetchBills = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/bills/', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('access_token')}`,  // Include the token
          },
        });
        setBills(response.data);
      } catch (error) {
        console.error('Failed to fetch bills:', error);
      }
    };
    fetchBills();
  }, []);

  return (
    <div className="container mt-5">
      <h2>Your Bills</h2>
      <ul className="list-group">
        {bills.map((bill) => (
          <li key={bill.id} className="list-group-item">
            <strong>Amount:</strong> ${bill.total_amount} | <strong>Due Date:</strong> {bill.due_date}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
